  try {
    const cosmetic = await axios({
      url: `https://fortniteapi.io/v2/items/list?name=${encodeURI(name)}&type.id=${type}`,
      headers: { Authorization: APIKEY },
    });
    return cosmetic.data.items[0];
  } catch (err) {
